import React, { useState } from 'react';
import './BurgerStyle.css'

const Burger = () => {
    const [check, setCheck] = useState(true);

    const click = event => {
        if (check === true)
            setCheck(false)
        else {
            setCheck(true)
        }
    }

    return (
        <div className={check === true ? 'icon burger_icon' : 'open icon burger_icon '} onClick={click}>
            <div>
                <span></span>
                <span></span>
                <span></span>
            </div>
            <div className={check === true ? 'hidden burger_dropdown' : 'shown burger_dropdown'}>
                <div className='burger_list_item'>Edit profile</div>
                <div className='burger_list_item'>L</div>
            </div>
        </div>
    )
}
export default Burger
