import React, { useState } from 'react';
import './BurgerStyle.css'

const Burger = () => {
    const [check, setCheck] = useState(true);
    const click = event => {
        if (check === true)
            setCheck(false)
        else {
            setCheck(true)
        }
    }

    return (
        
        <div className={check === true ? 'icon burger_icon' : 'open icon burger_icon '} onClick={click}>
            <span></span>
            <span></span>
            <span></span>
        </div>
    )
}
export default Burger
